	state_ |= has_playmod(p_, "Halo!") << 7;
	state_ |= has_playmod(p_, "duct tape") << 13;
	if (p_->pants == 9782) state_ |= 1 << 15;
	state_ |= has_playmod(p_, "Irradiated") << 19;
