void trigger_save_(bool restart_exe) {
	save_server_events();
	cout << "Saving: [PLAYERS/WORLDS/HONORS]" << endl;
	for (ENetPeer* currentPeer = server->peers; currentPeer < &server->peers[server->peerCount]; ++currentPeer) {
		if (currentPeer->state != ENET_PEER_STATE_CONNECTED or currentPeer->data == NULL) continue;
		//for (int i = 0; i < pInfo(currentPeer)->worlds_owned.size(); i++) if (get_world(pInfo(currentPeer)->worlds_owned[i]).owner_name != pInfo(currentPeer)->tankIDName) pInfo(currentPeer)->worlds_owned.erase(pInfo(currentPeer)->worlds_owned.begin() + i);
		save_player(pInfo(currentPeer), false);
	}
	f_saving_ = true;
	if (server_port == main_port) {
		ofstream m;
		m.open("C:/xampp/htdocs/growtopia/maintenance.txt"), m << "maint|`5The game is currently undergoing maintenance. We will be back soon.``", m.close();
	}
	Sleep(2000);
	cout << "Saving WORLDS [" << worlds.size() << "]" << endl;
	for (int i = 0; i < worlds.size(); i++) {
		save_world(worlds[i].name, false);
		//Sleep(50);
	}
	if (server_port == main_port) {
		ofstream m;
		m.open("C:/xampp/htdocs/growtopia/maintenance.txt"), m << "maint|Server is currently initializing or re-syncing with sub servers. Please try again in a minute.", m.close();
	}
	Sleep(2000);
	/*
	cout << "Saving GUILDS" << endl;
	if (server_port == main_port) save_guilds();
	Sleep(2000);*/
	cout << "Saved: [PLAYERS/WORLDS/HONORS]" << endl;
	Sleep(2000);
	if (restart_exe == false) {
		save_server_events();
	}
	else {
		worlds.clear();
		honors_reset();
		cout << "saved but did not restart" << endl;
		f_saving_ = false;
		if (server_port == main_port) {
			ofstream m;
			m.open("C:/xampp/htdocs/growtopia/maintenance.txt"), m << "", m.close();
		}
	}
	Sleep(20000);
	system("PAUSE");
	exit(0);
}


